mapper.select:
select * from job_snapshot

    1 = 1

		AND `id` = #{id}


		AND `job_info_id` = #{jobInfoId}


		AND `name` = #{name}


		AND `group` = #{group}


		AND `status` = #{status}


		AND ip = #{ip}


		AND url = #{url}


		AND create_time = #{createTime}


		AND modify_time = #{modifyTime}

select * from job_snapshot where 1=1

		AND `name` = #{name}


		AND `group` = #{group}


		AND `status` = #{status}

order by id desc

select * from job_snapshot where 1=1

		AND `name` = #{name}


		AND `group` = #{group}


		AND `status` = #{status}

order by id desc
limit #{limit}
